import React from "react";
import { useLocation } from "react-router-dom";

function Learn() {
  const { state } = useLocation();

  let kanjiReal = null;

  const getKanji = () => {
    let newReal = [];
    let kanjiArr = state.split("");
    for (let i = 0; i < kanjiArr.length; i++) {
      const options = {
        method: "GET",
        url:
          "https://kanjialive-api.p.rapidapi.com/api/public/kanji/" +
          kanjiArr[i],
        headers: {
          "X-RapidAPI-Key":
            "785c136fcdmsh0709f6e97484e7dp18472cjsnf05120b651f1",
          "X-RapidAPI-Host": "kanjialive-api.p.rapidapi.com",
        },
      };

      axios
        .request(options)
        .then(function (response) {
          //console.log(response.data);
          newReal.push(response.data);
        })
        .catch(function (error) {
          console.error(error);
        });
    }
    kanjiReal = newReal;
  };

  return (
    <div>
      {" "}
      {getKanji} {console.log(kanjiReal)} THIS IS THE LEARN PAGE
    </div>
  );
}

export default Learn;
