import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

function Learn() {
  const { state } = useLocation();
  const { lesson, level, kanji, kanjiStr } = state;

  let navigate = useNavigate();
  const navigateHome = () => navigate("/");

  const [index, setIndex] = useState(0);

  let done = false;
  const replay = (e) => {
    if (!done) {
      var video = document.getElementsByClassName("stroke");
      function reset(v) {
        v.addEventListener("ended", myHandler, false);
        function myHandler(e) {
          console.log("ended");
          setTimeout(function () {
            v.play();
          }, 3000);
        }
      }
      for (const el of video) {
        reset(el);
      }
      done = !done;
    }
  };

  const move = () => {
    setIndex(index + 1);
  };

  const moveBack = () => {
    setIndex(index - 1);
  };

  const toggleEngJpn = (event, num) => {
    document.getElementById("jpn" + num).style.display = "none";
    document.getElementById("eng" + num).style.display = "block";
  };

  useEffect(() => {
    var videos = document.getElementsByClassName("stroke");
    for (let i = 0; i < videos.length; i++) {
      videos[i].playbackRate = 1.25;
      videos[i].pause();
      videos[i].currentTime = 0;
      if (i === index) {
        videos[i].play();
      }
    }
    var boxes = document.getElementsByClassName("kanji-box");
    document.getElementById("fwd").style.visibility = "visible";
    document.getElementById("back").style.visibility = "visible";
    for (let i = 0; i < boxes.length; i++) {
      boxes[i].style.display = "none";
      if (i === index) {
        boxes[i].style.display = "block";
      }
    }
    if (index === boxes.length - 1) {
      document.getElementById("fwd").style.visibility = "hidden";
    } else if (index === 0) {
      document.getElementById("back").style.visibility = "hidden";
    }
  }, [index]);

  return (
    <div className="App">
      <div>
        <p>
          {" you chose L"}
          {lesson} and you are in JAPN {level}
          {"! "}
        </p>
        <p>the kanji in this lesson are: {kanjiStr}</p>
      </div>
      <div className="box-a-box">
        {kanji.map((item, idx) => {
          return (
            <div className="kanji-box" key={idx}>
              <div className="video">
                <video
                  muted
                  className="stroke"
                  onPlay={(e) => replay(e)}
                  width="190px"
                >
                  <source src={item.kanji.video.mp4} type="video/mp4" />
                </video>
              </div>
              <div className="info">
                <p>
                  kanji: <span>{item.kanji.character}</span>
                </p>
                <p>
                  meaning: <span>{item.kanji.meaning.english}</span>
                </p>
                <p>
                  kun: <span>{item.kanji.kunyomi.hiragana}</span>
                </p>
                <p>
                  on: <span>{item.kanji.onyomi.katakana}</span>
                </p>
              </div>
              <div className="examples">
                <p>
                  {item.examples.map((ex, idx) => {
                    return (
                      <div className="ex" key={idx}>
                        <div
                          className="ex-jpn"
                          id={"jpn" + idx}
                          onClick={(event) => toggleEngJpn(event, idx)}
                        >
                          {ex.japanese}
                        </div>
                        <div
                          className="ex-eng"
                          id={"eng" + idx}
                          onClick={(event) => toggleEngJpn(event, idx)}
                        >
                          {ex.meaning.english}
                        </div>
                      </div>
                    );
                  })}
                </p>
              </div>
            </div>
          );
        })}
      </div>
      <div>
        <button id="back" className="learn-btn" onClick={moveBack}>
          {"<"}
        </button>

        <button onClick={navigateHome}> home </button>

        <button id="fwd" className="learn-btn" onClick={move}>
          {">"}
        </button>
      </div>
    </div>
  );
}

export default Learn;
