import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

function Learn() {
  const { state } = useLocation();
  const { lesson, level, kanji } = state;

  let navigate = useNavigate();
  const navigateHome = () => navigate("/");

  const [index, setIndex] = useState(0);

  let done = false;
  const replay = (e) => {
    if (!done) {
      var video = document.getElementsByClassName("stroke");
      function reset(v) {
        v.addEventListener("ended", myHandler, false);
        function myHandler(e) {
          console.log("ended");
          setTimeout(function () {
            v.play();
          }, 3000);
        }
      }
      for (const el of video) {
        reset(el);
      }
      done = !done;
    }
  };

  const move = () => {
    setIndex(index + 1);
    console.log(index);
  };

  useEffect(() => {
    console.log("yo");
    var boxes = document.getElementsByClassName("kanji-box");
    for (let i = 0; i < boxes.length; i++) {
      boxes[i].style.visibility = "hidden";
      if (i === index) {
        console.log("A");
        boxes[i].style.visibility = "visible";
      }
    }
  }, [index]);

  return (
    <div className="App">
      <div>
        {" "}
        {console.log(kanji)} 勉強しましょうーyou chose L{lesson} and you are in
        JAPN {level}
        {"! "}
        <button onClick={navigateHome}> home </button>
      </div>
      <div>
        {kanji.map((item, idx) => {
          return (
            index === idx && (
              <div className="kanji-box" key={idx}>
                <div className="video">
                  <video
                    muted
                    className="stroke"
                    autoPlay
                    onPlay={(e) => replay(e)}
                    width="200px"
                  >
                    <source src={item.kanji.video.mp4} type="video/mp4" />
                  </video>
                </div>
                <div className="info">
                  <h3>kanji: {item.kanji.character}</h3>
                  <h3>meaning: {item.kanji.meaning.english}</h3>
                  <h3>kun: {item.kanji.kunyomi.hiragana}</h3>
                  <h3>on: {item.kanji.onyomi.katakana}</h3>
                </div>
              </div>
            )
          );
        })}
      </div>
      <div>
        <button className="learn-btn"> {"<"} </button>
        <button className="learn-btn" onClick={move}>
          {">"}
        </button>
      </div>
    </div>
  );
}

export default Learn;
