import "./App.css";
import React from "react";
import { useState, useEffect } from "react";

function App() {
  const [lessons, setLessons] = useState([]);

  const handleClick = (event, num) => {
    if (num === 1001) {
      setLessons(["L1", "L2", "L3", "L4", "L5"]);
    } else if (num === 1002) {
      setLessons(["L6", "L7", "L8", "L9", "L10"]);
    } else if (num === 2001) {
      setLessons(["L11", "L12", "L13", "L14", "L15"]);
    } else if (num === 2002) {
      setLessons(["L16", "L17", "L18", "L19", "L20"]);
    } else if (num === 3001) {
      setLessons(["L21", "L22", "L23", "L24", "L25"]);
    }
    document.getElementById("lesson").style.visibility = "visible";
  };

  useEffect(() => {
    showLessons();
  });

  const showLessons = () => {
    let string = "1";
    for (let i = 0; i < 5; i++) {
      document.getElementById(string).innerText = lessons[i];
      string += "1";
    }
  };

  return (
    <div className="App">
      <div>
        <div className="header">
          <h1>tekkanji</h1>
          <h2>テック + かんじ</h2>
          <h3>a kanji learning system for JAPN at Georgia Tech</h3>
        </div>
        <div className="class">
          <div>which class?</div>
          <div className="classOptions">
            <button onClick={(event) => handleClick(event, 1001)}>
              japn 1001
            </button>
            <button onClick={(event) => handleClick(event, 1002)}>
              japn 1002
            </button>
            <button onClick={(event) => handleClick(event, 2001)}>
              japn 2001
            </button>
            <button onClick={(event) => handleClick(event, 2002)}>
              japn 2002
            </button>
            <button onClick={(event) => handleClick(event, 3001)}>
              japn 3001
            </button>
          </div>

          <div className="lesson" id="lesson">
            <div>which lesson?</div>

            <div className="lessonOptions">
              <button id="1"></button>
              <button id="11"></button>
              <button id="111"></button>
              <button id="1111"></button>
              <button id="11111"></button>
            </div>
          </div>
          <div className="lor">
            <div>learn or review?</div>
            <div className="classOptions">
              <button>learn</button>
              <button>review</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
