        setKanji("一二三四五六七八九十");
        break;
      case 6: // this is L6B
        setKanji("学生先私外大小");
        break;

      // JAPN 1002
      case 7:
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      /* case 8:
        setKanji(["一", "二", "三", "四", "五", "六", "七", "八", "九", "十"]);
        break;
      case 9:
        setKanji(["一", "二", "三", "四", "五", "六", "七", "八", "九", "十"]);
        break;
      case 10:
        setKanji(["一", "二", "三", "四", "五", "六", "七", "八", "九", "十"]);
        break;
      case 11:
        setKanji(["一", "二", "三", "四", "五", "六", "七", "八", "九", "十"]);
        break;

      //JAPN 2001
      case 12:
