        break;
      case 16:
        setKanji(["一", "二", "三", "四", "五", "六", "七", "八", "九", "十"]);
        break;

      // JAPN 2002
      case 17:
        setKanji(["一", "二", "三", "四", "五", "六", "七", "八", "九", "十"]);
        break;
      case 18:
