                    <button
                      id="11111"
                      className="lessonOpt"
                      onClick={(event) => lessonClick(event, lessons[4])}
                    ></button>
                  )}
                </div>
              </div>

              <div className="lor" id="lor">
                <div>learn or review?</div>
                <div className="classOptions">
                  <button
                    className="lorOpt"
                    onClick={(event) => lorClick(event, 1)}
                  >
                    learn
                  </button>
                  <button
                    className="lorOpt"
                    onClick={(event) => lorClick(event, 2)}
                  >
                    review
                  </button>
                </div>
              </div>
            </div>

            <div>
              <button onClick={getKanji} className="leggo" id="leggo">
                行きましょう！
              </button>
            </div>
          </div>
        )}
      {lor === 1 && kanji && (
        <div className="learn">HELLO THIS IS THE LEARN PAGE</div>
      )}
    </div>
  );
}

export default App;
