import React from "react";
import { useLocation } from "react-router-dom";

function Learn() {
  const { state } = useLocation();
  const { lesson, level, kanji } = state;

  const replay = (e) => {
    var video = document.getElementsByClassName("stroke");
    console.log("yo");
    function reset(v) {
      v.addEventListener("ended", myHandler(e), false);
      function myHandler(e) {
        setTimeout(function () {
          v.play();
        }, 3000);
      }
    }
    for (const el of video) {
      reset(el);
    }
  };

  return (
    <div className="App">
      <div>
        {" "}
        {console.log(kanji)} LEARNING! いらっしゃいませ〜! you chose L{lesson}{" "}
        and you are in JAPN {level}
        {". "}
        {kanji.map((item, idx) => {
          return (
            <div className="kanji-box" key={idx}>
              <h3>kanji: {item.kanji.character}</h3>
              <h3>meaning: {item.kanji.meaning.english}</h3>
              <h3>kun: {item.kanji.kunyomi.hiragana}</h3>
              <video
                muted
                className="stroke"
                autoPlay
                onClick={(e) => replay(e)}
              >
                <source src={item.kanji.video.mp4} type="video/mp4" />
              </video>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Learn;
