import "./App.css";
import React from "react";
import { useState, useEffect } from "react";

function App() {
  const [lessons, setLessons] = useState([]);
  const [level, setLevel] = useState(0);
  const [lesson, setLesson] = useState(0);
  const [lor, setLor] = useState(0);
  const [isActive, setActive] = useState(false);

  const classClick = (event, num) => {
    setLevel(num);
    if (num === 1001) {
      setLessons([1, 2, 3, 4, 5]);
    } else if (num === 1002) {
      setLessons([6, 7, 8, 9, 10]);
    } else if (num === 2001) {
      setLessons([11, 12, 13, 14, 15]);
    } else if (num === 2002) {
      setLessons([16, 17, 18, 19, 20]);
    } else if (num === 3001) {
      setLessons([21, 22, 23, 24, 25]);
    }
    document.getElementById("lesson").style.visibility = "visible";
    toggleClass();
  };

  const lessonClick = (event, num) => {
    setLesson(num);
    document.getElementById("lor").style.visibility = "visible";
  };

  const lorClick = (event, num) => {
    setLor(num);
    document.getElementById("leggo").style.visibility = "visible";
  };

  useEffect(() => {
    showLessons();
  });

  const showLessons = () => {
    let string = "1";
    for (let i = 0; i < 5; i++) {
      document.getElementById(string).innerText = "L" + lessons[i];
      string += "1";
    }
  };

  const toggleClass = () => {
    setActive(!isActive);
  };

  return (
    <div className="App">
      <div>
        <div className="header">
          <h1>tekkanji</h1>
          <h2>テック + かんじ</h2>
          <h3>a kanji learning system for JAPN at Georgia Tech</h3>
        </div>
        <div className="class">
          <div>which class?</div>
          <div className="classOptions">
            <button
              className={isActive ? "active" : null}
              onClick={(event) => classClick(event, 1001)}
            >
              japn 1001
            </button>
            <button onClick={(event) => classClick(event, 1002)}>
              japn 1002
            </button>
            <button onClick={(event) => classClick(event, 2001)}>
              japn 2001
            </button>
            <button onClick={(event) => classClick(event, 2002)}>
              japn 2002
            </button>
            <button onClick={(event) => classClick(event, 3001)}>
              japn 3001
            </button>
          </div>

          <div className="lesson" id="lesson">
            <div>which lesson?</div>

            <div className="lessonOptions">
              <button
                id="1"
                onClick={(event) => lessonClick(event, lessons[0])}
              ></button>
              <button
                id="11"
                onClick={(event) => lessonClick(event, lessons[1])}
              ></button>
              <button
                id="111"
                onClick={(event) => lessonClick(event, lessons[2])}
              ></button>
              <button
                id="1111"
                onClick={(event) => lessonClick(event, lessons[3])}
              ></button>
              <button
                id="11111"
                onClick={(event) => lessonClick(event, lessons[4])}
              ></button>
            </div>
          </div>

          <div className="lor" id="lor">
            <div>learn or review?</div>
            <div className="classOptions">
              <button onClick={(event) => lorClick(event, 1)}>learn</button>
              <button onClick={(event) => lorClick(event, 2)}>review</button>
            </div>
          </div>
        </div>

        <div>
          <button className="leggo" id="leggo">
            行きましょう！
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
