import React from "react";
import { useLocation } from "react-router-dom";

function Learn() {
  const { state } = useLocation();
  const { lesson, level, kanji } = state;

  let done = false;
  const replay = (e) => {
    if (!done) {
      var video = document.getElementsByClassName("stroke");
      console.log(video);
      function reset(v) {
        v.addEventListener("ended", myHandler, false);
        function myHandler(e) {
          console.log("ended");
          setTimeout(function () {
            v.play();
          }, 3000);
        }
      }
      for (const el of video) {
        reset(el);
      }
      done = !done;
    }
  };

  return (
    <div className="App">
      <div>
        {" "}
        {console.log(kanji)} LEARNING! いらっしゃいませ〜! you chose L{lesson}{" "}
        and you are in JAPN {level}
        {". "}
        {kanji.map((item, idx) => {
          return (
            <div className="kanji-box" key={idx}>
              <div className="video">
                <video
                  muted
                  className="stroke"
                  autoPlay
                  onPlay={(e) => replay(e)}
                  width="200px"
                >
                  <source src={item.kanji.video.mp4} type="video/mp4" />
                </video>
              </div>
              <div className="info">
                <h3>kanji: {item.kanji.character}</h3>
                <h3>meaning: {item.kanji.meaning.english}</h3>
                <h3>kun: {item.kanji.kunyomi.hiragana}</h3>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Learn;
