import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

function Learn() {
  const { state } = useLocation();
  const { lesson, level, kanji, kanjiStr } = state;

  let navigate = useNavigate();
  const navigateHome = () => navigate("/");

  const [index, setIndex] = useState(0);

  let done = false;
  const replay = (e) => {
    if (!done) {
      var video = document.getElementsByClassName("stroke");
      function reset(v) {
        v.addEventListener("ended", myHandler, false);
        function myHandler(e) {
          console.log("ended");
          setTimeout(function () {
            v.play();
          }, 3000);
        }
      }
      for (const el of video) {
        reset(el);
      }
      done = !done;
    }
  };

  const move = () => {
    setIndex(index + 1);
  };

  const moveBack = () => {
    setIndex(index - 1);
  };

  useEffect(() => {
    var boxes = document.getElementsByClassName("kanji-box");
    for (let i = 0; i < boxes.length; i++) {
      boxes[i].style.display = "none";
      document.getElementById("fwd").display = "block";
      document.getElementById("back").display = "block";
      if (i === index) {
        boxes[i].style.display = "block";
      }
    }
    if (index === boxes.length - 1) {
      document.getElementById("fwd").display = "none";
    } else if (index === 0) {
      document.getElementById("back").display = "none";
    }
  }, [index]);

  return (
    <div className="App">
      <div>
        <p>
          {" you chose L"}
          {lesson} and you are in JAPN {level}
          {"! "}
        </p>
        <p>the kanji in this lesson are: {kanjiStr}</p>
      </div>
      <div>
        {kanji.map((item, idx) => {
          return (
            <div className="kanji-box" key={idx}>
              <div className="video">
                <video
                  muted
                  className="stroke"
                  autoPlay
                  onPlay={(e) => replay(e)}
                  width="200px"
                >
                  <source src={item.kanji.video.mp4} type="video/mp4" />
                </video>
              </div>
              <div className="info">
                <h3>kanji: {item.kanji.character}</h3>
                <h3>meaning: {item.kanji.meaning.english}</h3>
                <h3>kun: {item.kanji.kunyomi.hiragana}</h3>
                <h3>on: {item.kanji.onyomi.katakana}</h3>
              </div>
            </div>
          );
        })}
      </div>
      <div>
        <button id="back" className="learn-btn" onClick={moveBack}>
          {"<"}
        </button>
        <button onClick={navigateHome}> home </button>
        {index != 0 && (
          <button id="fwd" className="learn-btn" onClick={move}>
            {">"}
          </button>
        )}
      </div>
    </div>
  );
}

export default Learn;
