import React from "react";
import { useLocation } from "react-router-dom";

function Learn() {
  const { state } = useLocation();
  const { lesson, level, kanji } = state;

  const test = (item) => {
    document
      .getElementById("stroke")
      .addEventListener("ended", myHandler, false);
    function myHandler(e) {
      setTimeout(function () {
        console.log("ended");
        document.getElementById("stroke").play();
      }, 10000);
    }
    return (
      <video autoPlay muted loop id="stroke">
        <source src={item.kanji.video.mp4} type="video/mp4" />
      </video>
    );
  };

  return (
    <div className="App">
      <div>
        {" "}
        {console.log(kanji)} LEARNING! いらっしゃいませ〜! you chose L{lesson}{" "}
        and you are in JAPN {level}
        {". "}
        {kanji.map((item, idx) => {
          return (
            <div className="kanji-box" key={idx}>
              <h3>kanji: {item.kanji.character}</h3>
              <h3>meaning: {item.kanji.meaning.english}</h3>
              <h3>kun: {item.kanji.kunyomi.hiragana}</h3>
              {test(item)}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Learn;
