import { useState, useEffect } from "react";
import axios from "axios";

const Kanji = (props) => {
  const getKanji = () => {
    let newReal = [];
    let kanjiArr = kanji.split("");
    for (let i = 0; i < kanjiArr.length; i++) {
      const options = {
        method: "GET",
        url:
          "https://kanjialive-api.p.rapidapi.com/api/public/kanji/" +
          kanjiArr[i],
        headers: {
          "X-RapidAPI-Key":
            "785c136fcdmsh0709f6e97484e7dp18472cjsnf05120b651f1",
          "X-RapidAPI-Host": "kanjialive-api.p.rapidapi.com",
        },
      };

      axios
        .request(options)
        .then(function (response) {
          //console.log(response.data);
          newReal.push(response.data);
        })
        .catch(function (error) {
          console.error(error);
        });
    }
    kanjiReal = newReal;
  };

  return (
    <div>
      <p>Meaning: {props.children.kanji.meaning.english}</p>
      <p>Reading: {props.children.kanji.reading}</p>
    </div>
  );
};

export default Kanji;
