import "./App.css";
import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [lessons, setLessons] = useState([]);
  const [level, setLevel] = useState(0);
  const [lesson, setLesson] = useState(0);
  const [lor, setLor] = useState(0);
  const [kanji, setKanji] = useState([]);
  const [kanjiReal, setKanjiReal] = useState([]);

  function getKanji() {
    whichKanji();
    for (let i = 0; i < kanji.length; i++) {
      const options = {
        method: "GET",
        url:
          "https://kanjialive-api.p.rapidapi.com/api/public/kanji/" + kanji[i],
        headers: {
          "X-RapidAPI-Key":
            "785c136fcdmsh0709f6e97484e7dp18472cjsnf05120b651f1",
          "X-RapidAPI-Host": "kanjialive-api.p.rapidapi.com",
        },
      };

      axios
        .request(options)
        .then(function (response) {
          console.log(response.data);
          setKanjiReal([...kanjiReal, response.data]);
        })
        .catch(function (error) {
          console.error(error);
        });
    }
  }

  function whichKanji() {
    switch (lesson) {
      case 4:
        setKanji(["田", "中", "日", "本", "山", "上", "下", "人"]);
        break;
      case 5:
        setKanji(["一", "二", "三", "四", "五", "六", "七", "八", "九", "十"]);
        break;
      default:
        console.log("err");
    }
  }

  const classClick = (event, num) => {
    setLevel(num);
    toggleColor(event, "classOpt");
    if (num === 1001) {
      setLessons([4, 5, 6]);
    } else if (num === 1002) {
      setLessons([7, 8, 9, 10, 11]);
    } else if (num === 2001) {
      setLessons([12, 13, 14, 15, 16]);
    } else if (num === 2002) {
      setLessons([17, 18, 19, 20, 21]);
    } else if (num === 3001) {
      setLessons([22, 23, 24, 25]);
    }
    document.getElementById("lesson").style.visibility = "visible";
  };

  const lessonClick = (event, num) => {
    setLesson(num);
    toggleColor(event, "lessonOpt");
    document.getElementById("lor").style.visibility = "visible";
  };

  const lorClick = (event, num) => {
    setLor(num);
    toggleColor(event, "lorOpt");
    document.getElementById("leggo").style.visibility = "visible";
  };

  const toggleColor = (event, option) => {
    var btn = document.getElementsByClassName(option);
    for (let i = 0; i < btn.length; i++) {
      btn[i].classList.remove("active");
    }
    event.currentTarget.classList.add("active");
    if (option === "classOpt") {
      toggleColor(event, "lessonOpt");
    }
  };

  useEffect(() => {
    showLessons();
  });

  useEffect(() => {
    if (lor) {
      getKanji();
    }
    //eslint-disable-next-line react-hooks/exhaustive-deps
  }, [lor]);

  const showLessons = () => {
    console.log(level);
    if (level === 1001) {
      document.getElementById("1").innerText = "L5";
      document.getElementById("11").innerText = "L6A";
      document.getElementById("111").innerText = "L6B";
    } else if ([1002, 2001, 2002, 3001].includes(level)) {
      let string = "1";
      for (let i = 0; i < lessons.length; i++) {
        document.getElementById(string).innerText = "L" + lessons[i];
        string += "1";
      }
    }
  };

  return (
    <div className="App">
      <div className="Home">
        <div className="header">
          <h1>tekkanji</h1>
          <h2>テック + かんじ</h2>
          <h3>a kanji learning system for JAPN at Georgia Tech</h3>
        </div>
        <div className="class">
          <div>which class?</div>
          <div className="classOptions">
            <button
              className="classOpt"
              onClick={(event) => classClick(event, 1001)}
            >
              japn 1001
            </button>
            <button
              className="classOpt"
              onClick={(event) => classClick(event, 1002)}
            >
              japn 1002
            </button>
            <button
              className="classOpt"
              onClick={(event) => classClick(event, 2001)}
            >
              japn 2001
            </button>
            <button
              className="classOpt"
              onClick={(event) => classClick(event, 2002)}
            >
              japn 2002
            </button>
            <button
              className="classOpt"
              onClick={(event) => classClick(event, 3001)}
            >
              japn 3001
            </button>
          </div>

          <div className="lesson" id="lesson">
            <div>which lesson?</div>

            <div className="lessonOptions">
              <button
                id="1"
                className="lessonOpt"
                onClick={(event) => lessonClick(event, lessons[0])}
              ></button>
              <button
                id="11"
                className="lessonOpt"
                onClick={(event) => lessonClick(event, lessons[1])}
              ></button>
              <button
                id="111"
                className="lessonOpt"
                onClick={(event) => lessonClick(event, lessons[2])}
              ></button>
              {!(level === 1001) && (
                <button
                  id="1111"
                  className="lessonOpt"
                  onClick={(event) => lessonClick(event, lessons[3])}
                ></button>
              )}
              {!(level === 3001) && !(level === 1001) && (
                <button
                  id="11111"
                  className="lessonOpt"
                  onClick={(event) => lessonClick(event, lessons[4])}
                ></button>
              )}
            </div>
          </div>

          <div className="lor" id="lor">
            <div>learn or review?</div>
            <div className="classOptions">
              <button
                className="lorOpt"
                onClick={(event) => lorClick(event, 1)}
              >
                learn
              </button>
              <button
                className="lorOpt"
                onClick={(event) => lorClick(event, 2)}
              >
                review
              </button>
            </div>
          </div>
        </div>

        <div>
          <button onClick={getKanji} className="leggo" id="leggo">
            行きましょう！
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
