import "./App.css";
import React from "react";
import { useState } from "react";

function App() {
  const [lessons, setLessons] = useState([]);

  function handleClick(num) {
    if (num === 1001) {
      setLessons(["L1", "L2", "L3", "L4", "L5"]);
    }
  }

  return (
    <div className="App">
      <div>
        <div className="header">
          <h1>tekkanji</h1>
          <h2>テック + かんじ</h2>
          <h3>a kanji learning system for JAPN at Georgia Tech</h3>
        </div>
        <div className="class">
          <div>which class?</div>
          <div className="classOptions">
            <button onClick={handleClick}>japn 1001</button>
            <button>japn 1002</button>
            <button>japn 2001</button>
            <button>japn 2002</button>
            <button>japn 3001</button>
          </div>

          <div className="class">
            <div>which lesson?</div>

            <div className="1001options">
              <button>L1</button>
              <button>L2</button>
              <button>L3</button>
              <button>L4</button>
              <button>L5</button>
            </div>
          </div>
          <div className="class">
            <div>learn or review?</div>
            <div className="classOptions">
              <button>learn</button>
              <button>review</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
