import "./App.css";
import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [lessons, setLessons] = useState([]);
  const [level, setLevel] = useState(0);
  const [lesson, setLesson] = useState(0);
  const [lor, setLor] = useState(0);
  const [kanji, setKanji] = useState("");
  const [kanjiReal, setKanjiReal] = useState([]);

  function whichKanji() {
    switch (lesson) {
      // JAPN 1001
      case "5":
        setKanji("田中日本山上下人");
        break;
      case "6A":
        setKanji("一二三四五六七八九十");
        break;
      case "6B":
        setKanji("学生先私外大小");
        break;

      // JAPN 1002
      case "7":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      //need to update
      case "8":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "9":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "10":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "11":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;

      //JAPN 2001
      case "12":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "13":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "14":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "15":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "16":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;

      // JAPN 2002
      case "17":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "18":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "19":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "20":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "21":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;

      // JAPN 3001
      case "22":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "23":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "24":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      case "25":
        setKanji("今時月火水木金土曜半分週毎年間何");
        break;
      default:
        console.log("err");
    }
  }

  const kanjiInfo = kanjiReal.map((kanjiElement) => {
    return (
      <ul type="disc">
        <li
          style={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          {kanjiElement.kanji.meaning.english}
        </li>
        <li>{kanjiElement.kanji.reading}</li>
      </ul>
    );
  });

  function strokeOrder() {
    for (let i = 0; i < kanjiReal.length; i++) {
      console.log(kanjiReal[i].kanji.strokes);
    }
  } // i think we should use mapping now to make everything visible as react

  function getKanji() {
    let kanjiArr = kanji.split("");
    for (let i = 0; i < kanjiArr.length; i++) {
      const options = {
        method: "GET",
        url:
          "https://kanjialive-api.p.rapidapi.com/api/public/kanji/" + kanji[i],
        headers: {
          "X-RapidAPI-Key":
            "785c136fcdmsh0709f6e97484e7dp18472cjsnf05120b651f1",
          "X-RapidAPI-Host": "kanjialive-api.p.rapidapi.com",
        },
      };

      axios
        .request(options)
        .then(function (response) {
          //console.log(response.data);
          setKanjiReal([...kanjiReal, response.data]);
        })
        .catch(function (error) {
          console.error(error);
        });
    }
  }

  const classClick = (event, num) => {
    setLevel(num);
    toggleColor(event, "classOpt");
    if (num === 1001) {
      setLessons(["5", "6A", "6B"]);
    } else if (num === 1002) {
      setLessons(["7", "8", "9", "10", "11"]);
    } else if (num === 2001) {
      setLessons(["12", "13", "14", "15", "16"]);
    } else if (num === 2002) {
      setLessons(["17", "18", "19", "20", "21"]);
    } else if (num === 3001) {
      setLessons(["22", "23", "24", "25"]);
    }
    document.getElementById("lesson").style.visibility = "visible";
  };

  const lessonClick = (event, num) => {
    setLesson(num);
    toggleColor(event, "lessonOpt");
    document.getElementById("lor").style.visibility = "visible";
  };

  const lorClick = (event, num) => {
    setLor(num);
    toggleColor(event, "lorOpt");
    document.getElementById("leggo").style.visibility = "visible";
  };

  const toggleColor = (event, option) => {
    var btn = document.getElementsByClassName(option);
    for (let i = 0; i < btn.length; i++) {
      btn[i].classList.remove("active");
    }
    event.currentTarget.classList.add("active");
    if (option === "classOpt") {
      toggleColor(event, "lessonOpt");
    }
  };

  useEffect(() => {
    if (kanji === "") {
      showLessons();
    }
    //eslint-disable-next-line react-hooks/exhaustive-deps
  }, [level]);

  useEffect(() => {
    if (!(kanji === [])) {
      getKanji();
    }
    //eslint-disable-next-line react-hooks/exhaustive-deps
  }, [kanji]);

  const showLessons = () => {
    let string = "1";
    for (let i = 0; i < lessons.length; i++) {
      document.getElementById(string).innerText = "L" + lessons[i];
      string += "1";
    }
  };

  return (
    <div className="App">
      <div className="header">
        <h1>tekkanji</h1>
        <h2>テック + かんじ</h2>
        <h3>a kanji learning system for JAPN at Georgia Tech</h3>
      </div>
      {kanji === "" && (
        <div className="home">
          <div className="class">
            <div>which class?</div>
            <div className="classOptions">
              <button
                className="classOpt"
                onClick={(event) => classClick(event, 1001)}
              >
                japn 1001
              </button>
              <button
                className="classOpt"
                onClick={(event) => classClick(event, 1002)}
              >
                japn 1002
              </button>
              <button
                className="classOpt"
                onClick={(event) => classClick(event, 2001)}
              >
                japn 2001
              </button>
              <button
                className="classOpt"
                onClick={(event) => classClick(event, 2002)}
              >
                japn 2002
              </button>
              <button
                className="classOpt"
                onClick={(event) => classClick(event, 3001)}
              >
                japn 3001
              </button>
            </div>

            <div className="lesson" id="lesson">
              <div>which lesson?</div>

              <div className="lessonOptions">
                <button
                  id="1"
                  className="lessonOpt"
                  onClick={(event) => lessonClick(event, lessons[0])}
                ></button>
                <button
                  id="11"
                  className="lessonOpt"
                  onClick={(event) => lessonClick(event, lessons[1])}
                ></button>
                <button
                  id="111"
                  className="lessonOpt"
                  onClick={(event) => lessonClick(event, lessons[2])}
                ></button>
                {!(level === 1001) && (
                  <button
                    id="1111"
                    className="lessonOpt"
                    onClick={(event) => lessonClick(event, lessons[3])}
                  ></button>
                )}
                {!(level === 3001) && !(level === 1001) && (
                  <button
                    id="11111"
                    className="lessonOpt"
                    onClick={(event) => lessonClick(event, lessons[4])}
                  ></button>
                )}
              </div>
            </div>

            <div className="lor" id="lor">
              <div>learn or review?</div>
              <div className="classOptions">
                <button
                  className="lorOpt"
                  onClick={(event) => lorClick(event, 1)}
                >
                  learn
                </button>
                <button
                  className="lorOpt"
                  onClick={(event) => lorClick(event, 2)}
                >
                  review
                </button>
              </div>
            </div>
          </div>

          <div>
            <button onClick={whichKanji} className="leggo" id="leggo">
              行きましょう！
            </button>
          </div>
        </div>
      )}

      {lor === 1 && !(kanji === "") && (
        <div className="learn">
          いらっしゃいませ〜! you chose L{lesson} and you are in JAPN {level}
          {". "}
          {kanjiInfo}
        </div>
      )}

      {lor === 2 && !(kanji === "") && (
        <div className="learn">HELLO THIS IS THE REVIEW PAGE</div>
      )}
    </div>
  );
}

export default App;
