import "./App.css";
import React from "react";
import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Learn from "./pages/Learn";
import Review from "./pages/Review";

function App() {
  const [lessons, setLessons] = useState([]);
  const [level, setLevel] = useState(0);
  const [lesson, setLesson] = useState(0);
  const [lor, setLor] = useState(0);

  let navigate = useNavigate();

  const navigateTo = () => {
    if (lor === 1) {
      navigate("/learn");
    } else if (lor === 2) {
      navigate("/review");
    }
  };

  const classClick = (event, num) => {
    setLevel(num);
    toggleColor(event, "classOpt");
    if (num === 1001) {
      setLessons([1, 2, 3, 4, 5]);
    } else if (num === 1002) {
      setLessons([6, 7, 8, 9, 10]);
    } else if (num === 2001) {
      setLessons([11, 12, 13, 14, 15]);
    } else if (num === 2002) {
      setLessons([16, 17, 18, 19, 20]);
    } else if (num === 3001) {
      setLessons([21, 22, 23, 24, 25]);
    }
    document.getElementById("lesson").style.visibility = "visible";
  };

  const lessonClick = (event, num) => {
    setLesson(num);
    toggleColor(event, "lessonOpt");
    document.getElementById("lor").style.visibility = "visible";
  };

  const lorClick = (event, num) => {
    setLor(num);
    toggleColor(event, "lorOpt");
    document.getElementById("leggo").style.visibility = "visible";
  };

  const toggleColor = (event, option) => {
    var btn = document.getElementsByClassName(option);
    for (let i = 0; i < btn.length; i++) {
      btn[i].classList.remove("active");
    }
    event.currentTarget.classList.add("active");
    if (option === "classOpt") {
      toggleColor(event, "lessonOpt");
    }
  };

  useEffect(() => {
    showLessons();
  });

  const showLessons = () => {
    let string = "1";
    for (let i = 0; i < 5; i++) {
      document.getElementById(string).innerText = "L" + lessons[i];
      string += "1";
    }
  };

  return (
    <Router>
      <div className="App">
        <div>
          <div className="header">
            <h1>tekkanji</h1>
            <h2>テック + かんじ</h2>
            <h3>a kanji learning system for JAPN at Georgia Tech</h3>
          </div>
          <Routes>
            <Route path="/" element={<App />} />
            <Route path="/learn/:lesson" element={<Learn />} />
            <Route path="/review/:lesson" element={<Review />} />
            <div className="class">
              <div>which class?</div>
              <div className="classOptions">
                <button
                  className="classOpt"
                  onClick={(event) => classClick(event, 1001)}
                >
                  japn 1001
                </button>
                <button
                  className="classOpt"
                  onClick={(event) => classClick(event, 1002)}
                >
                  japn 1002
                </button>
                <button
                  className="classOpt"
                  onClick={(event) => classClick(event, 2001)}
                >
                  japn 2001
                </button>
                <button
                  className="classOpt"
                  onClick={(event) => classClick(event, 2002)}
                >
                  japn 2002
                </button>
                <button
                  className="classOpt"
                  onClick={(event) => classClick(event, 3001)}
                >
                  japn 3001
                </button>
              </div>

              <div className="lesson" id="lesson">
                <div>which lesson?</div>

                <div className="lessonOptions">
                  <button
                    id="1"
                    className="lessonOpt"
                    onClick={(event) => lessonClick(event, lessons[0])}
                  ></button>
                  <button
                    id="11"
                    className="lessonOpt"
                    onClick={(event) => lessonClick(event, lessons[1])}
                  ></button>
                  <button
                    id="111"
                    className="lessonOpt"
                    onClick={(event) => lessonClick(event, lessons[2])}
                  ></button>
                  <button
                    id="1111"
                    className="lessonOpt"
                    onClick={(event) => lessonClick(event, lessons[3])}
                  ></button>
                  <button
                    id="11111"
                    className="lessonOpt"
                    onClick={(event) => lessonClick(event, lessons[4])}
                  ></button>
                </div>
              </div>

              <div className="lor" id="lor">
                <div>learn or review?</div>
                <div className="classOptions">
                  <button
                    className="lorOpt"
                    onClick={(event) => lorClick(event, 1)}
                  >
                    learn
                  </button>
                  <button
                    className="lorOpt"
                    onClick={(event) => lorClick(event, 2)}
                  >
                    review
                  </button>
                </div>
              </div>
            </div>

            <div>
              <button onClick={navigateTo} className="leggo" id="leggo">
                行きましょう！
              </button>
            </div>
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
